	public List<Integer> findSubstring(String s, String[] words) {
		List<Integer> resultList = new ArrayList<Integer>();
		List<String> wordList = Arrays.asList(words);

		Set<String> allSet = new HashSet<String>();
		getAll(wordList, allSet, "");
		if (allSet.isEmpty()) {
			return resultList;
		}
		
		System.out.println("###..................." + allSet.size());
		
		int length = allSet.iterator().next().length();
		
		
		for (int i = 0; i < s.length() - length; i++) {
			String x = s.substring(i, i + length - 1);
			if(allSet.contains(x)){
				resultList.add(i);
			}
		}
		return resultList;
	}
	
	
	public void getAll(List<String> words, Set<String> allSet, String comStr) {
		System.out.println(comStr);
		if (words.isEmpty()) {
			allSet.add(comStr);
			return;
		}
		for (String s : words) {
			List<String> temp = new ArrayList<String>();
			temp.addAll(words);
			temp.remove(s);
			getAll(temp, allSet, comStr + s);
		}
	}
